import React from 'react';

export const Item = (props) => {
  // TODO: answer here
  const [item, setItem] = React.useState(0);
  return(
    <div className='each-box'>
      <img src={props.image} alt="each-item"/>
      <p>{props.title}</p>
      <div>
        <button
        aria-label={`minus-button-${props.id}`}
        onClick={()=>{
        // TODO: answer here
        if(item > 0){
          setItem(item-1);
          props.setTotal(props.total - 1);
        }
        }}
        >-</button>
        <input type="text" aria-label={`item-${props.id}`} className={`item-${props.id}`} value={item} disabled></input>
        <button
        aria-label={`add-button-${props.id}`}
        onClick={()=>{
          // TODO: answer here
          if(item < 10){
            setItem(item + 1);
            props.setTotal(props.total + 1);
          }else{
            props.showAlert(true)
          }
        }}
        >+</button>
      </div>
    </div>
  )
}


function App() {
  //Add state for total 
  // TODO: answer here
  const [total, setTotal] = React.useState(0);

  const dataDummy = [
    {
      id    : '1',
      image : "https://picsum.photos/id/0/150/",
      title : "Laptop",
    },
    {
      id    : '2',
      image : "https://picsum.photos/id/1010/150/",
      title : "Buku",
    },
    {
      id    : '3',
      image : "https://picsum.photos/id/103/150/",
      title : "Sepatu",
    }
  ];

  const showAlert = (isShow) => {
    if(isShow){
      alert("Ups, udah kelebihan yaa");
    }
  }

  return (
    <div className="state-props-2" aria-label="AppRoot">
      <h3>Keranjang Belanja</h3>
      <div className='box-container'>
          {dataDummy.map((element, index) => (
            // TODO: answer here
            <Item key={index} showAlert={showAlert} image={element.image} title={element.title} id={element.id} total={total} setTotal={setTotal} />
          ))}
      </div>
      <div className='end-section'>
          <h4>Kamu udah masukin&nbsp;
            <span className='total-item' aria-label="total-item">{total}</span>
            &nbsp;
          barang</h4>
      </div>
    </div>
  );
}

export default App;
